mapper.select:
  	select 
  	
  	from teaching_log where 1 =1 
  	 and TANENTID = #{leaseholderId} 
  	 and NJBH = #{njbh} 
  	 and NJMC like CONCAT('%',#{njmc},'%') 
  	 and BJBH = #{bjbh} 
  	 and BJMC like CONCAT('%',#{bjmc},'%') 
  	 and SKBH = #{skbh} 
  	 and SKSJ <= #{beginTime} 
  	 and SKSJ >= #{endTime} 
  	 and GH = #{gh} 
  	 and XM like CONCAT('%',#{xm},'%') 
  	 and XN = #{xn} 
  	 and SKBH in 
  		  
		  #{item}  
		 
  	
  	limit #{begin},#{size}

   SELECT count(1) FROM teaching_log 
	WHERE 1=1
	 and TANENTID = #{leaseholderId} 
  	 and NJBH = #{njbh} 
  	 and NJMC like CONCAT('%',#{njmc},'%') 
  	 and BJBH = #{bjbh} 
  	 and BJMC like CONCAT('%',#{bjmc},'%') 
  	 and SKBH = #{skbh} 
  	 and SKSJ <= #{beginTime} 
  	 and SKSJ >= #{endTime} 
  	 and GH = #{gh} 
  	 and XM like CONCAT('%',#{xm},'%') 
  	 and XN = #{xn} 
  	 and SKBH in 
  		  
		  #{item}

  	select log.TANENTID,log.SKBH,log.XN,log.NJBH,log.NJMC,log.BJBH,log.BJMC,log.XM,log.SKSJ,log.XXBH,log.XXMC,log.GH,
  	logCourse.KCXLBH,logCourse.KCXLMC,logCourse.KCBH,logCourse.KCMC
  	from teaching_log log left join teaching_log_course logCourse 
  	on log.SKBH = logCourse.SKBH where 1 = 1
  	 and log.TANENTID = #{leaseholderId} 
  	 and log.NJBH = #{njbh} 
  	 and log.NJMC like CONCAT('%',#{njmc},'%') 
  	 and log.BJBH = #{bjbh} 
  	 and log.BJMC like CONCAT('%',#{bjmc},'%') 
  	 and logCourse.KCXLBH = #{kcxlbh} 
  	 and logCourse.KCXLMC like CONCAT('%',#{kcxlmc},'%') 
  	 and logCourse.KCBH = #{kcbh} 
  	 and logCourse.KCMC like CONCAT('%',#{kcmc},'%') 
  	 and log.SKBH = #{skbh} 
  	 and log.SKSJ <= #{beginTime} 
  	 and log.SKSJ >= #{endTime} 
  	 and log.GH = #{gh} 
  	 and log.XM like CONCAT('%',#{xm},'%') 
  	 and log.XN = #{xn} 
  	 and log.SKBH in 
  		  
		  #{item}  
		 
  	
  	limit #{begin},#{size}

  select count(1) from teaching_log log left join teaching_log_course logCourse 
  	on log.SKBH = logCourse.SKBH where 1 = 1
	 and log.TANENTID = #{leaseholderId} 
  	 and log.NJBH = #{njbh} 
  	 and log.NJMC like CONCAT('%',#{njmc},'%') 
  	 and log.BJBH = #{bjbh} 
  	 and log.BJMC like CONCAT('%',#{bjmc},'%') 
  	 and logCourse.KCXLBH = #{kcxlbh} 
  	 and logCourse.KCXLMC like CONCAT('%',#{kcxlmc},'%') 
  	 and logCourse.KCBH = #{kcbh} 
  	 and logCourse.KCMC like CONCAT('%',#{kcmc},'%') 
  	 and log.SKBH = #{skbh} 
  	 and log.SKSJ <= #{beginTime} 
  	 and log.SKSJ >= #{endTime} 
  	 and log.GH = #{gh} 
  	 and log.XM like CONCAT('%',#{xm},'%') 
  	 and log.XN = #{xn} 
  	 and log.SKBH in 
  		  
		  #{item}
